import React, { useState, useRef, useEffect } from "react";
import "./ChatBot.css";

const ChatBot = () => {
  const [sessions, setSessions] = useState([{ id: 1, name: "Session 1", messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [input, setInput] = useState("");
  const [isListening, setIsListening] = useState(false);
  const chatBoxRef = useRef(null);
  const recognitionRef = useRef(null);

  const currentSession = sessions.find((s) => s.id === currentSessionId);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    updateSessionMessages([...currentSession.messages, userMessage]);
    setInput("");

    try {
      const res = await fetch("https://4cd1e6e50f99.ngrok-free.app/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });

      const data = await res.json();
      const botMessage = { sender: "bot", text: data.reply };
      updateSessionMessages([...currentSession.messages, userMessage, botMessage]);
    } catch {
      const errorMsg = { sender: "bot", text: "⚠️ Error connecting to the therapist." };
      updateSessionMessages([...currentSession.messages, userMessage, errorMsg]);
    }
  };

  const updateSessionMessages = (messages) => {
    setSessions((prev) =>
      prev.map((s) => (s.id === currentSessionId ? { ...s, messages } : s))
    );
  };

  useEffect(() => {
    chatBoxRef.current?.scrollTo({
      top: chatBoxRef.current.scrollHeight,
      behavior: "smooth",
    });
  }, [sessions]);

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  const createNewSession = () => {
    const newId = sessions.length ? sessions[sessions.length - 1].id + 1 : 1;
    const newSession = { id: newId, name: `Session ${newId}`, messages: [] };
    setSessions([...sessions, newSession]);
    setCurrentSessionId(newId);
  };

  const toggleListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech Recognition not supported in this browser.");
      return;
    }

    if (!recognitionRef.current) {
      const recognition = new SpeechRecognition();
      recognition.lang = "en-US";
      recognition.interimResults = false;
      recognition.maxAlternatives = 1;

      recognition.onresult = (e) => {
        const transcript = e.results[0][0].transcript;
        setInput((prev) => (prev ? prev + " " + transcript : transcript));
      };

      recognition.onerror = (e) => {
        console.error("Speech recognition error:", e.error);
        setIsListening(false);
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognitionRef.current = recognition;
    }

    if (!isListening) {
      recognitionRef.current.start();
      setIsListening(true);
    } else {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  return (
    <div className="chatbot-fullscreen">
      <aside className="session-panel">
        <h2>🗂 Sessions</h2>
        <ul>
          {sessions.map((session) => (
            <li
              key={session.id}
              className={session.id === currentSessionId ? "active" : ""}
              onClick={() => setCurrentSessionId(session.id)}
            >
              {session.name}
            </li>
          ))}
        </ul>
        <button className="new-session-btn" onClick={createNewSession}>
          + New Session
        </button>
      </aside>

      <div className="chatbot-container">
        <header className="chat-header">🧠 AI Therapist</header>
        <div className="chat-box" ref={chatBoxRef}>
          {currentSession?.messages.map((msg, idx) => (
            <div key={idx} className={`chat-message ${msg.sender}`}>
              <div className="chat-bubble">{msg.text}</div>
            </div>
          ))}
        </div>
        <div className="chat-input-area">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder="Type or use the mic..."
          />
          <button
            className={`mic-btn ${isListening ? "listening" : ""}`}
            onClick={toggleListening}
          >
            🎤
          </button>
          <button onClick={sendMessage}>➤</button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
