import React, { useState, useEffect, useRef } from 'react';
import '../styles/JournalPanel.css';

const JournalPanel = ({ task, onClose }) => {
  const panelRef = useRef(null);
  const [journals, setJournals] = useState([]);
  const [title, setTitle] = useState(task?.title || '');
  const [content, setContent] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const debounceTimer = useRef(null);

  // Load journals from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('journals');
    if (stored) setJournals(JSON.parse(stored));
  }, []);

  // Close panel on outside click
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (panelRef.current && !panelRef.current.contains(e.target)) onClose();
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [onClose]);

  // Debounced auto-save for new journal
  useEffect(() => {
    if (!title.trim() && !content.trim()) return;

    if (debounceTimer.current) clearTimeout(debounceTimer.current);

    debounceTimer.current = setTimeout(() => {
      const newJournal = {
        title: title.trim() || 'Untitled',
        time: new Date().toLocaleString(),
        content: content.trim(),
      };

      // Prevent saving empty content or duplicates
      if (!newJournal.content) return;

      const updated = [...journals, newJournal];
      localStorage.setItem('journals', JSON.stringify(updated));
      setJournals(updated);
      setTitle('');
      setContent('');
    }, 1000);

    return () => clearTimeout(debounceTimer.current);
  }, [title, content]);

  // Handle editing existing journals
  const handleEdit = (index, field, value) => {
    const updated = [...journals];
    updated[index][field] = value;
    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));
  };

  return (
    <div className="journal-panel-overlay">
      <div className="journal-panel" ref={panelRef}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>📝 New Journal</h3>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              color: 'hotpink',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
            }}
          >
            ✖
          </button>
        </div>

        <input
          placeholder="Journal Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="journal-title-input"
        />

        <textarea
          rows="6"
          placeholder="Start writing..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />

        <hr style={{ margin: '20px 0', opacity: 0.3 }} />

        <h4>📚 Previous Journals</h4>
        <div className="journal-history">
          {journals.length === 0 && <p>No journals yet.</p>}
          {journals.map((j, idx) => (
            <div key={idx} className="journal-entry">
              {editIndex === idx ? (
                <>
                  <input
                    className="journal-title-input"
                    value={j.title}
                    onChange={(e) => handleEdit(idx, 'title', e.target.value)}
                  />
                  <textarea
                    className="journal-edit-area"
                    value={j.content}
                    onChange={(e) => handleEdit(idx, 'content', e.target.value)}
                  />
                  <button
                    onClick={() => setEditIndex(null)}
                    style={{ marginTop: '6px' }}
                  >
                    ✅ Done
                  </button>
                </>
              ) : (
                <>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <strong>{j.title}</strong>
                    <button
                      className="edit-btn"
                      onClick={() => setEditIndex(idx)}
                    >
                      ✏️ Edit
                    </button>
                  </div>
                  <span style={{ fontSize: '12px', color: 'gray' }}>{j.time}</span>
                  <p style={{ whiteSpace: 'pre-wrap' }}>{j.content}</p>
                </>
              )}
              <hr />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default JournalPanel;
