      <summary className="dropdown-button">üß† CBT</summary>
      <ul className="dropdown-list">
        <li onClick={() => setMode('guided_journaling')}>üìù Guided Journaling</li>
        <li onClick={() => setMode('distortion_detection')}>üîç Distortion Detection</li>
        <li onClick={() => setMode('thought_deframing')}>üß† Thought Deframing</li>
        <li onClick={() => setMode('affirmation_separator')}>üåü Affirmation Separator</li>
      </ul>
    </details>
  </div>
);

export default ModeSelector;
