import React from 'react';

const MessageList = ({ messages, chatBoxRef }) => (
  <div className="chat-box" ref={chatBoxRef}>
    {messages?.map((msg, idx) => (
      <div key={idx} className={`message ${msg.sender}`}>{msg.text}</div>
    ))}
  </div>
);

export default MessageList;
