import React, { useEffect, useState } from 'react';
import '../styles/JournalsPage.css';

const JournalsPage = ({ onNew }) => {
  const [journals, setJournals] = useState([]);
  const [drafts, setDrafts] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem('journals');
    const storedDrafts = localStorage.getItem('journalDrafts');
    if (stored) setJournals(JSON.parse(stored));
    if (storedDrafts) setDrafts(JSON.parse(storedDrafts));

    // Warn user if leaving with unsaved draft
    const handleBeforeUnload = (e) => {
      if (drafts.length > 0) {
        e.preventDefault();
        e.returnValue = '';
      }
    };
    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, [drafts]);

  return (
    <div className="journals-page">
      <h2>üìì Your Journals</h2>
      <div className="journal-grid">
        {/* New Journal Card */}
        <div className="journal-card new-journal" onClick={onNew}>
          <div className="new-icon">‚ûï</div>
          <p>New Journal</p>
        </div>

        {/* Existing Journals */}
        {[...journals].reverse().map((j, idx) => (
          <div className="journal-card" key={idx}>
            <h4>{j.title}</h4>
            <p className="journal-snippet">{j.content.slice(0, 100)}{j.content.length > 100 ? '...' : ''}</p>
            <span className="timestamp">{j.time}</span>
          </div>
        ))}

        {/* Drafts */}
        {drafts.length > 0 && (
          <>
            <h3 style={{ gridColumn: '1 / -1', marginTop: '20px' }}>üìù Drafts</h3>
            {[...drafts].reverse().map((d, idx) => (
              <div className="journal-card draft" key={`d-${idx}`}> 
                <h4>{d.title || 'Untitled (Draft)'}</h4>
                <p className="journal-snippet">{d.content.slice(0, 100)}{d.content.length > 100 ? '...' : ''}</p>
                <span className="timestamp">{d.time}</span>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default JournalsPage;
