import React, { useState } from 'react';
import MicButton from './MicButton';
import '../styles/ChatBot.css';
import '../styles/ModeSelector.css';

const ChatInput = ({ input, setInput, onSend, onVoiceInput, setMode, onClear }) => {
  const [showDropdown, setShowDropdown] = useState(false);

  return (
    <div className="chat-input-area">
      {/* Dropdown inside input box */}
      <div className="mode-selector-inline">
        <button
          className="dropdown-trigger"
          onClick={() => setShowDropdown(!showDropdown)}
          onBlur={() => setTimeout(() => setShowDropdown(false), 200)}
        >
          üß† Modes ‚åÑ
        </button>

        {showDropdown && (
          <div className="dropdown-menu">
            <div className="dropdown-item" onClick={() => setMode('guided_journaling')}>
              üìù Guided Journaling
            </div>
            <div className="dropdown-item" onClick={() => setMode('distortion_detection')}>
              üîç Distortion Detection
            </div>
            <div className="dropdown-item" onClick={() => setMode('thought_deframing')}>
              üß† Thought Deframing
            </div>
            <div className="dropdown-item" onClick={() => setMode('affirmation_separator')}>
              üåü Affirmation Separator
            </div>
          </div>
        )}
      </div>

      {/* Text input */}
      <input
        type="text"
        placeholder="Ask me anything..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && onSend()}
      />

      {/* Send, Mic, Clear */}
      <button onClick={onSend} title="Send">‚û§</button>
      <MicButton onVoiceInput={onVoiceInput} />
      <button onClick={onClear} className="clear-chat-btn" title="Clear Chat">üóë</button>
    </div>
  );
};

export default ChatInput;
