import React, { useState } from 'react';
import '../styles/TaskPanel.css';

const defaultTasks = {
  guided_journaling: [
    { title: 'Write Your Feelings', time: 'Daily at 9:00am' },
    { title: 'Reflect on Wins', time: 'Every Evening' },
  ],
  distortion_detection: [
    { title: 'Identify Cognitive Distortions', time: 'Every Monday' },
  ],
  thought_deframing: [],
  affirmation_separator: [],
};

const TaskPanel = ({ mode, onClose }) => {
  const [tasks, setTasks] = useState(defaultTasks[mode] || []);
  const [newTask, setNewTask] = useState({ title: '', time: '' });
  const [showForm, setShowForm] = useState(false);

  const handleAddTask = () => {
    if (newTask.title && newTask.time) {
      setTasks((prev) => [...prev, newTask]);
      setNewTask({ title: '', time: '' });
      setShowForm(false);
    }
  };

  if (!mode) return null;

  return (
    <div className="task-panel">
      <button className="task-close-btn" onClick={onClose}>‚ùå</button>
      <h2 className="task-title">Tasks for {mode.replace(/_/g, ' ')}</h2>
      <button className="add-task-btn" onClick={() => setShowForm(!showForm)}>
        + Add New Task
      </button>

      {showForm && (
        <div className="task-form">
          <input
            placeholder="Task Title"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            placeholder="Time (e.g. Daily at 9am)"
            value={newTask.time}
            onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
          />
          <button onClick={handleAddTask}>Save</button>
        </div>
      )}

      <div className="task-cards">
        {tasks.map((task, idx) => (
          <div className="task-card" key={idx}>
            <h4>{task.title}</h4>
            <p>{task.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskPanel;
