import React, { useState, useRef, useEffect } from "react";
import "./ChatBot.css";

const ChatBot = () => {
  const [sessions, setSessions] = useState([{ id: 1, name: "Session 1", messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [input, setInput] = useState("");
  const chatBoxRef = useRef(null);

  const currentSession = sessions.find((s) => s.id === currentSessionId);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input };
    const updatedSessions = sessions.map((session) =>
      session.id === currentSessionId
        ? { ...session, messages: [...session.messages, userMessage] }
        : session
    );
    setSessions(updatedSessions);
    setInput("");

    try {
      const res = await fetch("https://4cd1e6e50f99.ngrok-free.app/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });

      const data = await res.json();
      const botMessage = { sender: "bot", text: data.reply };

      setSessions((prevSessions) =>
        prevSessions.map((session) =>
          session.id === currentSessionId
            ? { ...session, messages: [...session.messages, botMessage] }
            : session
        )
      );
    } catch {
      const errorMsg = { sender: "bot", text: "âš ï¸ Error connecting to the therapist." };

      setSessions((prevSessions) =>
        prevSessions.map((session) =>
          session.id === currentSessionId
            ? { ...session, messages: [...session.messages, errorMsg] }
            : session
        )
      );
    }
  };

  useEffect(() => {
    chatBoxRef.current?.scrollTo({
      top: chatBoxRef.current.scrollHeight,
      behavior: "smooth",
    });
  }, [sessions]);

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  const createNewSession = () => {
    const newId = sessions.length ? sessions[sessions.length - 1].id + 1 : 1;
    const newSession = { id: newId, name: `Session ${newId}`, messages: [] };
    setSessions([...sessions, newSession]);
    setCurrentSessionId(newId);
  };

  return (
    <div className="chatbot-fullscreen">
      {/* Session Manager */}
      <aside className="session-panel">
        <h2>ðŸ—‚ Sessions</h2>
        <ul>
          {sessions.map((session) => (
            <li
              key={session.id}
              className={session.id === currentSessionId ? "active" : ""}
              onClick={() => setCurrentSessionId(session.id)}
            >
              {session.name}
            </li>
          ))}
        </ul>
        <button className="new-session-btn" onClick={createNewSession}>+ New Session</button>
      </aside>

      {/* Chat Area */}
      <div className="chatbot-container">
        <header className="chat-header">ðŸ§  AI Therapist</header>
        <div className="chat-box" ref={chatBoxRef}>
          {currentSession?.messages.map((msg, index) => (
            <div key={index} className={`chat-message ${msg.sender}`}>
              <div className="chat-bubble">{msg.text}</div>
            </div>
          ))}
        </div>
        <div className="chat-input-area">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder="Type your message..."
          />
          <button onClick={sendMessage}>âž¤</button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
