import React, { useState, useEffect, useRef } from 'react';
import MicButton from './MicButton';
import ModeSelector from './ModeSelector';
import MessageList from './MessageList';
import ChatInput from './ChatInput';
import DarkModeToggle from './DarkModeToggle';
import TaskPanel from './TaskPanel';
import JournalPanel from './JournalPanel'; // ‚úÖ newly added

import '../styles/ChatBot.css';

const ChatBot = () => {
  const [input, setInput] = useState('');
  const [sessions, setSessions] = useState([{ id: 1, name: 'Session 1', messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [chatStarted, setChatStarted] = useState(false);
  const [mode, setMode] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [typing, setTyping] = useState(false);
  const [showJournal, setShowJournal] = useState(false);

  const chatBoxRef = useRef(null);

  const currentSession = sessions.find((s) => s.id === currentSessionId);

  useEffect(() => {
    chatBoxRef.current?.scrollTo({ top: chatBoxRef.current.scrollHeight, behavior: 'smooth' });
  }, [currentSession?.messages, typing]);

  useEffect(() => {
    if (mode === 'normal_journaling') {
      setShowJournal(true);
    } else {
      setShowJournal(false);
    }
  }, [mode]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'me', text: input };
    setInput('');
    setSessions((prev) =>
      prev.map((s) =>
        s.id === currentSessionId ? { ...s, messages: [...s.messages, userMessage] } : s
      )
    );

    if (!chatStarted) setChatStarted(true);
    setTyping(true);

    try {
      const response = await fetch('https://0efd7e6df07d.ngrok-free.app/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, mode }),
      });

      if (!response.ok) throw new Error('API error');
      const data = await response.json();

      setTimeout(() => {
        const aiMessage = { sender: 'ai', text: data.reply };
        setSessions((prev) =>
          prev.map((s) =>
            s.id === currentSessionId ? { ...s, messages: [...s.messages, aiMessage] } : s
          )
        );
        setTyping(false);
      }, 1200);
    } catch (err) {
      console.error('Connection error:', err);
      const fallback = {
        sender: 'ai',
        text: "‚ö†Ô∏è Sorry, I couldn't connect to the server. Try again later.",
      };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, fallback] } : s
        )
      );
      setTyping(false);
    }
  };

  const handleVoiceInput = (text) => {
    setInput(text);
    setTimeout(() => sendMessage(), 10);
  };

  const clearChat = () => {
    setSessions((prev) =>
      prev.map((s) =>
        s.id === currentSessionId ? { ...s, messages: [] } : s
      )
    );
    setChatStarted(false);
    chatBoxRef.current?.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Show task panel only for CBT task-related modes
  const isTaskMode = ['guided_journaling', 'distortion_detection', 'thought_deframing', 'affirmation_separator'].includes(mode);

  return (
    <div className={`chatbot-container ${darkMode ? 'dark' : ''}`}>
      <ModeSelector setMode={setMode} />
      <DarkModeToggle darkMode={darkMode} setDarkMode={setDarkMode} />

      <div className={`logo-container ${chatStarted ? 'minimized' : ''}`}>
        <video className="logo-video" src="/logo.mp4" autoPlay muted loop playsInline />
      </div>

      <div className="chat-area full-width">
        <MessageList messages={currentSession?.messages} chatBoxRef={chatBoxRef} typing={typing} />
        <ChatInput input={input} setInput={setInput} onSend={sendMessage} onVoiceInput={handleVoiceInput} />
        <button
          onClick={clearChat}
          style={{
            marginTop: '10px',
            fontSize: '12px',
            background: '#eee',
            borderRadius: '8px',
            padding: '6px 12px',
            border: '1px solid #ccc',
          }}
        >
          üóëÔ∏è Clear Chat
        </button>
      </div>

      {/* Show TaskPanel only for specific CBT modes */}
      {isTaskMode && <TaskPanel mode={mode} />}

      {/* Show JournalPanel only for 'normal_journaling' */}
      {showJournal && <JournalPanel onClose={() => setShowJournal(false)} />}
    </div>
  );
};

export default ChatBot;
