import React, { useState, useEffect, useRef } from 'react';
import '../styles/JournalPanel.css';

const JournalPanel = ({ task, onClose }) => {
  const panelRef = useRef(null);
  const [journals, setJournals] = useState([]);
  const [title, setTitle] = useState(task?.title || '');
  const [content, setContent] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const debounceTimer = useRef(null);

  // Load journals from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('journals');
    if (stored) setJournals(JSON.parse(stored));
  }, []);

  // Close journal on outside click
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (panelRef.current && !panelRef.current.contains(e.target)) {
        onClose();
      }
    };
    document.addEventListener('mousedown', handleClickOutside, true);
    return () => document.removeEventListener('mousedown', handleClickOutside, true);
  }, [onClose]);

  // Auto-save with debounce (new or latest entry)
  useEffect(() => {
    if (!title.trim() && !content.trim()) return;

    if (debounceTimer.current) clearTimeout(debounceTimer.current);

    debounceTimer.current = setTimeout(() => {
      const newJournal = {
        title: title.trim() || 'Untitled',
        time: new Date().toLocaleString(),
        content: content.trim(),
      };

      if (!newJournal.content) return;

      const updated = [...journals];
      if (
        updated.length > 0 &&
        updated[updated.length - 1].title === title &&
        updated[updated.length - 1].content === content
      ) {
        // No change ‚Äî skip
        return;
      }

      updated[updated.length - 1] = newJournal;
      localStorage.setItem('journals', JSON.stringify(updated));
      setJournals(updated);
    }, 800);

    return () => clearTimeout(debounceTimer.current);
  }, [title, content]);

  const handleEdit = (index, field, value) => {
    const updated = [...journals];
    updated[index][field] = value;
    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));
  };

  const handleDelete = (index) => {
    const confirmDelete = window.confirm('üóëÔ∏è Are you sure you want to delete this journal?');
    if (!confirmDelete) return;

    const updated = journals.filter((_, i) => i !== index);
    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));
    if (editIndex === index) setEditIndex(null);
  };

  return (
    <div className="journal-panel-overlay">
      <div className="journal-panel" ref={panelRef}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>üìù New Journal</h3>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              color: 'hotpink',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
            }}
          >
            ‚úñ
          </button>
        </div>

        <input
          placeholder="Journal Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="journal-title-input"
        />

        <textarea
          rows="6"
          placeholder="Start writing..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />

        <hr style={{ margin: '20px 0', opacity: 0.3 }} />

        <h4>üïì Previous Journals (Latest First)</h4>
        <div className="journal-history">
          {journals.length === 0 && <p>No journals yet.</p>}
          {[...journals].reverse().map((j, idx) => {
            const realIndex = journals.length - 1 - idx;
            return (
              <div key={realIndex} className="journal-entry">
                {editIndex === realIndex ? (
                  <>
                    <input
                      className="journal-title-input"
                      value={j.title}
                      onChange={(e) => handleEdit(realIndex, 'title', e.target.value)}
                    />
                    <textarea
                      className="journal-edit-area"
                      value={j.content}
                      onChange={(e) => handleEdit(realIndex, 'content', e.target.value)}
                    />
                    <div style={{ marginTop: '6px' }}>
                      <button onClick={() => setEditIndex(null)}>‚úÖ Done</button>
                    </div>
                  </>
                ) : (
                  <>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <strong>{j.title}</strong>
                      <div>
                        <button className="edit-btn" onClick={() => setEditIndex(realIndex)}>‚úèÔ∏è Edit</button>
                        <button className="delete-btn" onClick={() => handleDelete(realIndex)}>üóëÔ∏è Delete</button>
                      </div>
                    </div>
                    <span style={{ fontSize: '12px', color: 'gray' }}>{j.time}</span>
                    <p style={{ whiteSpace: 'pre-wrap' }}>{j.content}</p>
                  </>
                )}
                <hr />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default JournalPanel;
