import React, { useState, useEffect, useRef } from 'react';
// import SessionManager from './SessionManager'; // Optional
import MicButton from './MicButton';
import '../styles/ChatBot.css';

const ChatBot = () => {
  const [input, setInput] = useState('');
  const [sessions, setSessions] = useState([
    { id: 1, name: 'Session 1', messages: [] },
  ]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [chatStarted, setChatStarted] = useState(false);
  const chatBoxRef = useRef(null);

  const currentSession = sessions.find(
    (session) => session.id === currentSessionId
  );

  useEffect(() => {
    chatBoxRef.current?.scrollTo({
      top: chatBoxRef.current.scrollHeight,
      behavior: 'smooth',
    });
  }, [currentSession?.messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'me', text: input };
    setInput(''); // ✅ Clear input immediately

    const updatedSessions = sessions.map((session) =>
      session.id === currentSessionId
        ? { ...session, messages: [...session.messages, userMessage] }
        : session
    );
    setSessions(updatedSessions);

    if (!chatStarted) setChatStarted(true);

    try {
      const response = await fetch('https://your-ngrok-url/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input }),
      });

      if (!response.ok) throw new Error('API error');

      const data = await response.json();
      const aiMessage = { sender: 'ai', text: data.reply };

      setSessions((prev) =>
        prev.map((session) =>
          session.id === currentSessionId
            ? {
                ...session,
                messages: [...session.messages, aiMessage],
              }
            : session
        )
      );
    } catch (err) {
      console.error('Error connecting to AI:', err);

      const fallbackMessage = {
        sender: 'ai',
        text: "⚠️ Sorry, I couldn't connect to the server. Please try again later.",
      };

      setSessions((prev) =>
        prev.map((session) =>
          session.id === currentSessionId
            ? {
                ...session,
                messages: [...session.messages, fallbackMessage],
              }
            : session
        )
      );
    }
  };

  const handleVoiceInput = (text) => setInput(text);

  return (
    <div className="chatbot-container">
      {/* Animated Logo */}
      <div className={`logo-container ${chatStarted ? 'minimized' : ''}`}>
        <video
          className="logo-video"
          src="/logo.mp4"
          autoPlay
          muted
          loop
          playsInline
        />
      </div>

      <div className="chat-area full-width">
        <div className="chat-box" ref={chatBoxRef}>
          {currentSession?.messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender}`}>
              {msg.text}
            </div>
          ))}
        </div>

        <div className="chat-input-area">
          <input
            type="text"
            placeholder="Ask me anything..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          />
          <MicButton onVoiceInput={handleVoiceInput} />
          <button onClick={sendMessage}>➤</button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
