import React, { useState, useEffect, useRef } from 'react';
import Sidebar from './Sidebar';
import MessageList from './MessageList';
import ChatInput from './ChatInput';
import TaskPanel from './TaskPanel';
import JournalPanel from './JournalPanel';
import DarkModeToggle from './DarkModeToggle';

import '../styles/ChatBot.css';

const ChatBot = () => {
  const [input, setInput] = useState('');
  const [sessions, setSessions] = useState([{ id: 1, name: 'Session 1', messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [chatStarted, setChatStarted] = useState(false);
  const [mode, setMode] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [typing, setTyping] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [view, setView] = useState('chat');
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const chatBoxRef = useRef(null);
  const currentSession = sessions.find((s) => s.id === currentSessionId);

  useEffect(() => {
    chatBoxRef.current?.scrollTo({ top: chatBoxRef.current.scrollHeight, behavior: 'smooth' });
  }, [currentSession?.messages, typing]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'me', text: input };
    setInput('');
    setSessions((prev) =>
      prev.map((s) =>
        s.id === currentSessionId ? { ...s, messages: [...s.messages, userMessage] } : s
      )
    );

    if (!chatStarted) setChatStarted(true);
    setTyping(true);

    try {
      const response = await fetch('https://your-ngrok-url/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, mode }),
      });

      if (!response.ok) throw new Error("Server Error");

      const data = await response.json();

      const aiMessage = { sender: 'ai', text: data.reply };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, aiMessage] } : s
        )
      );
    } catch (err) {
      const fallback = {
        sender: 'ai',
        text: "⚠️ Sorry, I couldn't connect to the server. Try again later.",
      };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, fallback] } : s
        )
      );
    }

    setTyping(false);
  };

  return (
    <div className={`chatbot-container ${darkMode ? 'dark' : ''}`}>
      {/* Sidebar */}
      <Sidebar onSelect={setView} selected={view} />

      {/* Sidebar Toggle Button */}
      <button
        className={`sidebar-toggle ${sidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}
        onClick={() => setSidebarOpen(!sidebarOpen)}
      >
        {sidebarOpen ? '⬅️' : '➡️'}
      </button>

      {/* Main Content */}
      <div className={`main-content ${sidebarOpen ? '' : 'expanded'}`}>
        <DarkModeToggle darkMode={darkMode} setDarkMode={setDarkMode} />

        {view === 'chat' && (
          <div className="chat-area">
            <MessageList messages={currentSession?.messages} chatBoxRef={chatBoxRef} typing={typing} />
            <ChatInput
              input={input}
              setInput={setInput}
              onSend={sendMessage}
              onVoiceInput={(text) => {
                setInput(text);
                setTimeout(sendMessage, 10);
              }}
            />
            <button
              className="clear-chat-btn"
              onClick={() => {
                setSessions((prev) =>
                  prev.map((s) =>
                    s.id === currentSessionId ? { ...s, messages: [] } : s
                  )
                );
                setChatStarted(false);
                chatBoxRef.current?.scrollTo({ top: 0, behavior: 'smooth' });
              }}
            >
              🗑️ Clear Chat
            </button>
          </div>
        )}

        {view === 'tasks' && (
          <TaskPanel
            mode={mode}
            onClose={() => setView('chat')}
            onTaskClick={(task) => {
              setSelectedTask(task);
              setView('journals');
            }}
          />
        )}

        {view === 'journals' && (
          <JournalPanel task={selectedTask} onClose={() => setView('chat')} />
        )}

        {view === 'newJournal' && (
          <JournalPanel onClose={() => setView('chat')} />
        )}

        {view === 'cbt' && (
          <div style={{ color: 'white' }}>
            <h2>🧠 CBT Modes</h2>
            <p>Select from thought deframing, journaling, affirmations etc.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatBot;
