import React, { useState, useEffect, useRef } from 'react';
import '../styles/JournalPanel.css';

const JournalPanel = ({ task, onClose }) => {
  const [entry, setEntry] = useState('');
  const [journals, setJournals] = useState([]);
  const panelRef = useRef(null);

  // Load journals from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('journals');
    if (stored) {
      setJournals(JSON.parse(stored));
    }
  }, []);

  // Close panel when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (panelRef.current && !panelRef.current.contains(event.target)) {
        onClose();
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [onClose]);

  const saveEntry = () => {
    const newJournal = {
      title: task?.title || 'Manual Entry',
      time: new Date().toLocaleString(),
      content: entry,
    };
    const updated = [...journals, newJournal];
    localStorage.setItem('journals', JSON.stringify(updated));
    setJournals(updated);
    setEntry('');
  };

  return (
    <div className="journal-panel-overlay">
      <div className="journal-panel" ref={panelRef}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>Journal: {task?.title || 'New Journal'}</h3>
          <button
            onClick={onClose}
            style={{ background: 'transparent', color: 'hotpink', border: 'none', fontSize: '20px', cursor: 'pointer' }}
          >
            âœ–
          </button>
        </div>

        <textarea
          rows="8"
          placeholder="Write your thoughts here..."
          value={entry}
          onChange={(e) => setEntry(e.target.value)}
        />

        <button className="save-journal" onClick={saveEntry}>
          Save Entry
        </button>

        <hr style={{ margin: '20px 0', opacity: 0.3 }} />

        <h4>ðŸ“š Previous Journals</h4>
        <div className="journal-history">
          {journals.length === 0 && <p>No journals yet.</p>}
          {journals.map((j, idx) => (
            <div key={idx} className="journal-entry">
              <strong>{j.title}</strong>{' '}
              <span style={{ fontSize: '12px', color: 'gray' }}>{j.time}</span>
              <p>{j.content}</p>
              <hr />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default JournalPanel;
