import React, { useState, useEffect } from 'react';
import '../styles/JournalPanel.css';

const JournalPanel = ({ task, onClose }) => {
  const [entry, setEntry] = useState('');
  const [savedJournals, setSavedJournals] = useState([]);
  const [showAll, setShowAll] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);

  const getKey = (taskTitle) => `journal_${taskTitle.replace(/\s+/g, '_').toLowerCase()}`;
  const storageKey = getKey(task?.title || '');

  // Load existing entry on open
  useEffect(() => {
    const saved = localStorage.getItem(storageKey);
    if (saved) setEntry(saved);
  }, [storageKey]);

  // Auto-save after pause (1.5s delay)
  useEffect(() => {
    if (!entry) return;
    if (typingTimeout) clearTimeout(typingTimeout);

    const timeout = setTimeout(() => {
      localStorage.setItem(storageKey, entry);
      console.log('üìù Auto-saved');
    }, 1500);

    setTypingTimeout(timeout);
    return () => clearTimeout(timeout);
  }, [entry]);

  // Save on manual click
  const handleSave = () => {
    localStorage.setItem(storageKey, entry);
    alert('‚úÖ Journal entry saved locally!');
  };

  // Load all journal keys
  const handleViewAll = () => {
    const all = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key.startsWith('journal_')) {
        all.push({
          title: key.replace('journal_', '').replace(/_/g, ' '),
          text: localStorage.getItem(key),
        });
      }
    }
    setSavedJournals(all);
    setShowAll(true);
  };

  return (
    <div className="journal-panel-overlay">
      <div className="journal-panel">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>Journal: {task?.title}</h3>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              color: 'hotpink',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
            }}
          >
            ‚úñ
          </button>
        </div>

        {!showAll ? (
          <>
            <textarea
              rows="12"
              placeholder="Vent out your thoughts here..."
              value={entry}
              onChange={(e) => setEntry(e.target.value)}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between', gap: '10px' }}>
              <button className="save-journal" onClick={handleSave}>
                üíæ Save Entry
              </button>
              <button className="view-journals" onClick={handleViewAll}>
                üìö View All Journals
              </button>
            </div>
          </>
        ) : (
          <div className="all-journals-list">
            <h4>üóÇÔ∏è All Saved Journals</h4>
            <div style={{ maxHeight: '300px', overflowY: 'auto', marginTop: '10px' }}>
              {savedJournals.length === 0 && <p>No journals found.</p>}
              {savedJournals.map((j, idx) => (
                <div
                  key={idx}
                  style={{
                    background: 'rgba(255,255,255,0.08)',
                    padding: '12px',
                    marginBottom: '10px',
                    borderRadius: '10px',
                  }}
                >
                  <strong>{j.title}</strong>
                  <p style={{ fontSize: '13px', whiteSpace: 'pre-wrap' }}>{j.text}</p>
                </div>
              ))}
            </div>
            <button onClick={() => setShowAll(false)} style={{ marginTop: '10px' }}>
              üîô Back to Journal
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default JournalPanel;
