import React, { useState } from 'react';
import '../styles/Sidebar.css';

const Sidebar = ({ onSelect, selected }) => {
  const [collapsed, setCollapsed] = useState(false);

  const handleToggle = () => setCollapsed(!collapsed);

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <button
        className="sidebar-toggle"
        onClick={handleToggle}
        title={collapsed ? 'Open sidebar' : 'Close sidebar'}
      >
        {collapsed ? '‚ò∞' : '‚úñ'}
      </button>

      {!collapsed && (
        <ul className="sidebar-menu">
          <li className={selected === 'chat' ? 'active' : ''} onClick={() => onSelect('chat')}>
            üí¨ <span className="label">Chat</span>
          </li>
          <li className={selected === 'tasks' ? 'active' : ''} onClick={() => onSelect('tasks')}>
            üóÇÔ∏è <span className="label">Tasks</span>
          </li>
          <li className={selected === 'journals' ? 'active' : ''} onClick={() => onSelect('journals')}>
            üìì <span className="label">Journals</span>
          </li>
          <li className={selected === 'newJournal' ? 'active' : ''} onClick={() => onSelect('newJournal')}>
            ‚ûï <span className="label">New Journal</span>
          </li>
          <li className={selected === 'cbt' ? 'active' : ''} onClick={() => onSelect('cbt')}>
            üß† <span className="label">CBT Modes</span>
          </li>
        </ul>
      )}
    </div>
  );
};

export default Sidebar;
