import React, { useState, useEffect, useRef } from 'react';
import MicButton from './MicButton';
import '../styles/ChatBot.css';

const ChatBot = () => {
  const [input, setInput] = useState('');
  const [sessions, setSessions] = useState([{ id: 1, name: 'Session 1', messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [chatStarted, setChatStarted] = useState(false);
  const [mode, setMode] = useState('guided_journaling');
  const chatBoxRef = useRef(null);

  const currentSession = sessions.find((s) => s.id === currentSessionId);

  useEffect(() => {
    chatBoxRef.current?.scrollTo({ top: chatBoxRef.current.scrollHeight, behavior: 'smooth' });
  }, [currentSession?.messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'me', text: input };
    setInput('');

    setSessions((prev) =>
      prev.map((s) =>
        s.id === currentSessionId ? { ...s, messages: [...s.messages, userMessage] } : s
      )
    );

    if (!chatStarted) setChatStarted(true);

    try {
      const response = await fetch('https://39b48d3e635b.ngrok-free.app/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, mode }),
      });

      if (!response.ok) throw new Error('API error');
      const data = await response.json();
      const aiMessage = { sender: 'ai', text: data.reply };

      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, aiMessage] } : s
        )
      );
    } catch (err) {
      console.error('Connection error:', err);
      const fallback = {
        sender: 'ai',
        text: "⚠️ Sorry, I couldn't connect to the server. Try again later.",
      };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, fallback] } : s
        )
      );
    }
  };

  const handleVoiceInput = (text) => {
    setInput(text);
    setTimeout(() => sendMessage(), 10);
  };

  return (
    <div className="chatbot-container">
      <div className={`logo-container ${chatStarted ? 'minimized' : ''}`}>
        <video
          className="logo-video"
          src="/logo.mp4"
          autoPlay
          muted
          loop
          playsInline
        />
      </div>

      <div className="chat-area full-width">
        <div className="chat-box" ref={chatBoxRef}>
          {currentSession?.messages.map((msg, idx) => (
            <div key={idx} className={`message ${msg.sender}`}>{msg.text}</div>
          ))}
        </div>

        <div className="chat-input-area">
          <select
            className="mode-selector"
            value={mode}
            onChange={(e) => setMode(e.target.value)}
            title="Switch CBT Mode"
          >
            <option value="guided_journaling">📝 Guided Journaling</option>
            <option value="distortion_detection">🔍 Distortion Detection</option>
            <option value="thought_deframing">🧠 Thought Deframing</option>
            <option value="affirmation_separator">🌟 Affirmation Separator</option>
          </select>

          <input
            type="text"
            placeholder="Ask me anything..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          />
          <MicButton onVoiceInput={handleVoiceInput} />
          <button onClick={sendMessage}>➤</button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
