import React, { useState } from 'react';
import '../styles/Sidebar.css';

const Sidebar = ({ onSelect, selected }) => {
  const [collapsed, setCollapsed] = useState(false);

  const handleToggle = () => setCollapsed(!collapsed);

  const menuItems = [
    { key: 'chat', label: 'Chat', icon: 'üí¨' },
    { key: 'tasks', label: 'Tasks', icon: 'üóÇÔ∏è' },
    { key: 'journals', label: 'Journals', icon: 'üìì' },
    { key: 'newJournal', label: 'New Journal', icon: '‚ûï' },
    { key: 'cbt', label: 'CBT Modes', icon: 'üß†' },
  ];

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <button
        className="sidebar-toggle"
        onClick={handleToggle}
        title={collapsed ? 'Open sidebar' : 'Close sidebar'}
      >
        {collapsed ? '‚ò∞' : '‚úñ'}
      </button>

      <ul className="sidebar-menu">
        {menuItems.map((item) => (
          <li
            key={item.key}
            className={selected === item.key ? 'active' : ''}
            onClick={() => onSelect(item.key)}
            title={item.label} // Tooltip when collapsed
          >
            <span className="icon">{item.icon}</span>
            {!collapsed && <span className="label">{item.label}</span>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
