import React, { useState, useRef, useEffect } from 'react';
import MicButton from './MicButton';
import '../styles/ChatBot.css';
import '../styles/ModeSelector.css';

const modeMap = {
  'guided_journaling': '📝 Guided Journaling',
  'distortion_detection': '🔍 Distortion Detection',
  'thought_deframing': '🧠 Thought Deframing',
  'affirmation_separator': '🌟 Affirmation Separator'
};

const ChatInput = ({ input, setInput, onSend, onVoiceInput, setMode }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedMode, setSelectedMode] = useState('');
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleModeSelect = (mode) => {
    setMode(mode);
    setSelectedMode(mode);
    setShowDropdown(false);
  };

  const handleSend = () => {
    if (!input.trim()) return;
    // Optionally prepend a tone message based on mode
    if (selectedMode === 'guided_journaling') {
      setInput(`Here's my journal entry:
${input}`);
    } else if (selectedMode === 'distortion_detection') {
      setInput(`Can you check if this has cognitive distortions?
${input}`);
    } else if (selectedMode === 'thought_deframing') {
      setInput(`Help me reframe this thought:
${input}`);
    } else if (selectedMode === 'affirmation_separator') {
      setInput(`Please extract affirmations from this:
${input}`);
    }
    setTimeout(onSend, 0);
  };

  return (
    <div className="chat-input-area responsive-chat-input">
      <div className="mode-selector-inline" ref={dropdownRef}>
        <button
          className="dropdown-trigger"
          onClick={() => setShowDropdown((prev) => !prev)}
        >
          {selectedMode ? modeMap[selectedMode] : '🧠 Modes'}
        </button>

        {showDropdown && (
          <div className="dropdown-menu">
            {Object.entries(modeMap).map(([key, label]) => (
              <div
                key={key}
                className="dropdown-item"
                onClick={() => handleModeSelect(key)}
              >
                {label}
              </div>
            ))}
          </div>
        )}
      </div>

      <input
        type="text"
        placeholder="Ask me anything..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && handleSend()}
      />
      <button onClick={handleSend}>➤</button>
      <MicButton onVoiceInput={(text) => {
        setInput(text);
        setTimeout(handleSend, 10);
      }} />
    </div>
  );
};

export default ChatInput;
