import React, { useState, useEffect, useRef } from 'react';
import Sidebar from './Sidebar';
import BookTherapistPage from './BookTherapistPage';
import SessionSummaryPage from './SessionSummaryPage';
import ChatInput from './ChatInput';
import TaskPanel from './TaskPanel';
import JournalPanel from './JournalPanel';
import JournalsPage from './JournalsPage';
import DarkModeToggle from './DarkModeToggle';
import '../styles/ChatBot.css';

const ChatBot = () => {
  const [input, setInput] = useState('');
  const [sessions, setSessions] = useState([{ id: 1, name: 'Session 1', messages: [] }]);
  const [currentSessionId, setCurrentSessionId] = useState(1);
  const [chatStarted, setChatStarted] = useState(false);
  const [mode, setMode] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [typing, setTyping] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [view, setView] = useState('chat');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [voices, setVoices] = useState([]);
  const [selectedVoice, setSelectedVoice] = useState(null);

  const chatBoxRef = useRef(null);
  const currentSession = sessions.find((s) => s.id === currentSessionId);

  useEffect(() => {
    chatBoxRef.current?.scrollTo({ top: chatBoxRef.current.scrollHeight, behavior: 'smooth' });
  }, [currentSession?.messages, typing]);

  // Load voices
  useEffect(() => {
    const loadVoices = () => {
      const availableVoices = window.speechSynthesis.getVoices();
      setVoices(availableVoices);

      // Select Indian female voice as default
      const defaultVoice = availableVoices.find((voice) =>
        voice.name.toLowerCase().includes('kalpana') ||
        voice.name.toLowerCase().includes('heera') ||
        voice.name.toLowerCase().includes('google hindi') ||
        voice.name.toLowerCase().includes('english (india)')
      );
      if (defaultVoice) setSelectedVoice(defaultVoice);
    };

    loadVoices();
    window.speechSynthesis.onvoiceschanged = loadVoices;
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'me', text: input };
    setInput('');
    setSessions((prev) =>
      prev.map((s) =>
        s.id === currentSessionId ? { ...s, messages: [...s.messages, userMessage] } : s
      )
    );

    if (!chatStarted) setChatStarted(true);
    setTyping(true);

    try {
      const response = await fetch('https://5ef2e9bd1b39.ngrok-free.app/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, mode }),
      });

      if (!response.ok) throw new Error("Server Error");

      const data = await response.json();

      const aiMessage = { sender: 'ai', text: data.reply };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, aiMessage] } : s
        )
      );
    } catch (err) {
      const fallback = {
        sender: 'ai',
        text: "‚ö†Ô∏è Sorry, I couldn't connect to the server. Try again later.",
      };
      setSessions((prev) =>
        prev.map((s) =>
          s.id === currentSessionId ? { ...s, messages: [...s.messages, fallback] } : s
        )
      );
    }

    setTyping(false);
  };

  const speakText = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'en-IN';

    if (selectedVoice) {
      utterance.voice = selectedVoice;
    }

    speechSynthesis.speak(utterance);
  };

  const renderMessagesWithAudio = () => {
    return currentSession?.messages.map((msg, index) => (
      <div key={index} className={`message ${msg.sender}`}>
        <div className="bubble">{msg.text}</div>
        {msg.sender === 'ai' && (
          <button
            className="speak-button"
            onClick={() => speakText(msg.text)}
            title={`Read in ${selectedVoice?.name || 'Default Voice'}`}
          >
            üîä
          </button>
        )}
      </div>
    ));
  };

  return (
    <div className={`chatbot-container ${darkMode ? 'dark' : ''}`}>
      <Sidebar
        onSelect={(val) => {
          setView(val);
          setSelectedTask(null);
        }}
        selected={view}
      />

      <button
        className={`sidebar-toggle ${sidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}
        onClick={() => setSidebarOpen(!sidebarOpen)}
      >
        {sidebarOpen ? '‚¨ÖÔ∏è' : '‚û°Ô∏è'}
      </button>

      <div className={`main-content ${sidebarOpen ? '' : 'expanded'}`}>
        <DarkModeToggle
          darkMode={darkMode}
          setDarkMode={setDarkMode}
          sidebarOpen={sidebarOpen}
        />

        {/* Voice Dropdown */}
        <div style={{ padding: '10px' }}>
          <label style={{ marginRight: '8px', fontWeight: 'bold' }}>Voice:</label>
          <select
            value={selectedVoice?.name || ''}
            onChange={(e) => {
              const voice = voices.find((v) => v.name === e.target.value);
              setSelectedVoice(voice);
            }}
          >
            {voices.map((voice, idx) => (
              <option key={idx} value={voice.name}>
                {voice.name} ({voice.lang})
              </option>
            ))}
          </select>
        </div>

        {view === 'chat' && (
          <div className="chat-area">
            <div className="chat-box" ref={chatBoxRef}>
              {renderMessagesWithAudio()}
              {typing && (
                <div className="message ai">
                  <div className="bubble">Typing...</div>
                </div>
              )}
            </div>

            <ChatInput
              input={input}
              setInput={setInput}
              onSend={sendMessage}
              setMode={setMode}
              onVoiceInput={(text) => {
                setInput(text); // ‚õî Don't auto-send after voice input
              }}
            />
          </div>
        )}

        {view === 'tasks' && (
          <TaskPanel
            mode={mode}
            onClose={() => setView('chat')}
            onTaskClick={(task) => setSelectedTask(task)}
          />
        )}

        {view === 'journals' && !selectedTask && (
          <JournalsPage onNew={() => setView('newJournal')} />
        )}

        {view === 'journals' && selectedTask && (
          <JournalPanel task={selectedTask} onClose={() => setSelectedTask(null)} />
        )}

        {view === 'newJournal' && (
          <JournalPanel task={{ title: '' }} onClose={() => setView('journals')} />
        )}

        {view === 'bookTherapist' && <BookTherapistPage />}
        {view === 'sessionSummary' && <SessionSummaryPage />}
      </div>
    </div>
  );
};

export default ChatBot;
