import React, { useState, useRef, useEffect } from 'react';
import MicButton from './MicButton';
import '../styles/ChatBot.css';
import '../styles/ModeSelector.css';

const ChatInput = ({ input, setInput, onSend, onVoiceInput, setMode }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="chat-input-area">
      {/* Dropdown inside input area */}
      <div className="mode-selector-inline" ref={dropdownRef}>
        <button
          className="dropdown-trigger"
          onClick={() => setShowDropdown((prev) => !prev)}
        >
          üß† Modes ‚åÑ
        </button>

        {showDropdown && (
          <div className="dropdown-menu">
            <div className="dropdown-item" onClick={() => { setMode('guided_journaling'); setShowDropdown(false); }}>
              üìù Guided Journaling
            </div>
            <div className="dropdown-item" onClick={() => { setMode('distortion_detection'); setShowDropdown(false); }}>
              üîç Distortion Detection
            </div>
            <div className="dropdown-item" onClick={() => { setMode('thought_deframing'); setShowDropdown(false); }}>
              üß† Thought Deframing
            </div>
            <div className="dropdown-item" onClick={() => { setMode('affirmation_separator'); setShowDropdown(false); }}>
              üåü Affirmation Separator
            </div>
          </div>
        )}
      </div>

      <input
        type="text"
        placeholder="Ask me anything..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && onSend()}
      />
      <button onClick={onSend}>‚û§</button>
      <MicButton onVoiceInput={onVoiceInput} />
    </div>
  );
};

export default ChatInput;
