import React from 'react';
import '../styles/Sidebar.css';

const Sidebar = ({ onSelect, setMode, sidebarOpen }) => {
  return (
    <div className={`sidebar ${sidebarOpen ? 'open' : 'collapsed'}`}>
      <ul className="sidebar-menu">
        <li onClick={() => onSelect('chat')} title="Chat History">üí¨ {sidebarOpen && 'Chat'}</li>
        <li onClick={() => onSelect('tasks')} title="Tasks">üìã {sidebarOpen && 'Tasks'}</li>
        <li onClick={() => onSelect('journals')} title="Journals">üìì {sidebarOpen && 'Journals'}</li>
        <li onClick={() => {
          onSelect('journals');
          setMode('');
        }} title="New Journal">‚ûï {sidebarOpen && 'New Journal'}</li>

        {sidebarOpen && (
          <li className="dropdown-wrapper">
            <details className="dropdown">
              <summary className="dropdown-button">üß† CBT Modes</summary>
              <ul className="dropdown-list">
                <li onClick={() => setMode('guided_journaling')}>üìù Guided Journaling</li>
                <li onClick={() => setMode('distortion_detection')}>üîç Distortion Detection</li>
                <li onClick={() => setMode('thought_deframing')}>üß† Thought Deframing</li>
                <li onClick={() => setMode('affirmation_separator')}>üåü Affirmation Separator</li>
              </ul>
            </details>
          </li>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
