import React, { useState, useEffect, useRef } from 'react';
import '../styles/JournalPanel.css';

const JournalPanel = ({ task, onClose }) => {
  const panelRef = useRef(null);
  const debounceTimer = useRef(null);
  const hasSavedRef = useRef(false);

  const [journals, setJournals] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const [isNewJournal, setIsNewJournal] = useState(true);

  // Load from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('journals');
    if (stored) setJournals(JSON.parse(stored));
  }, []);

  // Set title/content if a task is passed
  useEffect(() => {
    if (task?.title) {
      setTitle(task.title);
      setContent('');
      setIsNewJournal(true);
      setEditIndex(null);
    }
  }, [task]);

  // Debounced save
  useEffect(() => {
    if (!title.trim() && !content.trim()) return;

    if (debounceTimer.current) clearTimeout(debounceTimer.current);

    debounceTimer.current = setTimeout(() => {
      saveJournal(false);
    }, 800);

    hasSavedRef.current = false;

    return () => clearTimeout(debounceTimer.current);
  }, [title, content]);

  // Click outside to save and close
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (panelRef.current && !panelRef.current.contains(e.target)) {
        saveJournal(true);
        onClose();
      }
    };

    document.addEventListener('mousedown', handleClickOutside, true);
    return () => document.removeEventListener('mousedown', handleClickOutside, true);
  }, [title, content, journals]);

  const saveJournal = (forcePush) => {
    const trimmedTitle = title.trim();
    const trimmedContent = content.trim();
    if (!trimmedTitle && !trimmedContent) return;
    if (hasSavedRef.current && !forcePush) return;

    const newJournal = {
      title: trimmedTitle || 'Untitled',
      content: trimmedContent,
      time: new Date().toLocaleString(),
    };

    let updated = [...journals];

    if (!isNewJournal && editIndex !== null) {
      // Editing existing
      updated[editIndex] = newJournal;
    } else {
      const last = updated[updated.length - 1];
      const isDuplicate =
        last &&
        last.title === newJournal.title &&
        last.content === newJournal.content;

      if (!isDuplicate) {
        updated.push(newJournal);
      }
    }

    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));

    if (forcePush) {
      setTitle('');
      setContent('');
      setIsNewJournal(true);
      setEditIndex(null);
    }

    hasSavedRef.current = true;
  };

  const handleEdit = (index, field, value) => {
    const updated = [...journals];
    updated[index][field] = value;
    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));
  };

  const handleDelete = (index) => {
    const confirmDelete = window.confirm('🗑️ Are you sure you want to delete this journal?');
    if (!confirmDelete) return;

    const updated = journals.filter((_, i) => i !== index);
    setJournals(updated);
    localStorage.setItem('journals', JSON.stringify(updated));
    if (editIndex === index) setEditIndex(null);
  };

  return (
    <div className="journal-panel-overlay">
      <div className="journal-panel" ref={panelRef}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <h3>📝 New Journal</h3>
          <button
            onClick={() => {
              saveJournal(true);
              onClose();
            }}
            style={{
              background: 'transparent',
              color: 'hotpink',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
            }}
          >
            ✖
          </button>
        </div>

        <input
          placeholder="Journal Title"
          value={title}
          onChange={(e) => {
            setTitle(e.target.value);
            setIsNewJournal(true);
          }}
          className="journal-title-input"
        />

        <textarea
          rows="6"
          placeholder="Start writing..."
          value={content}
          onChange={(e) => {
            setContent(e.target.value);
            setIsNewJournal(true);
          }}
        />

        <hr style={{ margin: '20px 0', opacity: 0.3 }} />
        <h4>🕓 Previous Journals</h4>

        <div className="journal-history">
          {journals.length === 0 && <p>No journals yet.</p>}
          {[...journals].reverse().map((j, idx) => {
            const realIndex = journals.length - 1 - idx;
            return (
              <div key={realIndex} className="journal-entry">
                {editIndex === realIndex ? (
                  <>
                    <input
                      className="journal-title-input"
                      value={j.title}
                      onChange={(e) => handleEdit(realIndex, 'title', e.target.value)}
                    />
                    <textarea
                      className="journal-edit-area"
                      value={j.content}
                      onChange={(e) => handleEdit(realIndex, 'content', e.target.value)}
                    />
                    <button onClick={() => setEditIndex(null)}>✅ Done</button>
                  </>
                ) : (
                  <>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <strong>{j.title}</strong>
                      <div>
                        <button className="edit-btn" onClick={() => {
                          setEditIndex(realIndex);
                          setIsNewJournal(false);
                        }}>✏️ Edit</button>
                        <button className="delete-btn" onClick={() => handleDelete(realIndex)}>🗑️ Delete</button>
                      </div>
                    </div>
                    <span style={{ fontSize: '12px', color: 'gray' }}>{j.time}</span>
                    <p style={{ whiteSpace: 'pre-wrap' }}>{j.content}</p>
                  </>
                )}
                <hr />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default JournalPanel;
